package Arrays;

public class Mathes {
	public  static int FindMax(int a,int b,int c) {
		return Math.max(a,Math.max(b, c));
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Mathes m=new Mathes();
		int a=2;
		int b=3;
		int c=6;
		System.out.println(FindMax(a,b ,c ))
		;

	}

}
