public class Swap_two_num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		 a=a+b;//10+5=15
		 b=a-b;//15-5
		 a=a-b;//15-10
		System.out.println("a:"+a);
		System.out.println("b:"+b);
		

	}

}
